import React from "react";


class Model{
   constructor() {
      this.notes = []
   }
}

var model = new Model();

class Memo extends React.Component{
   constructor() {
      super();
      this.state = {
         userInput: '',
         color: 'skyblue',
         alert: ''
      };
   }


   //creates list of added notes
   render (){
      let listAddedNotes = Model.notes.map(function(note){
         if(note.id != null){
            return(
               <div>
                  <div className={note.bgColor + `note-box alert col-md-11`}>
                  {note.content}
               </div>
               <div className="col-md-1 text-center">
                  <button className="delete btn btn-default" onClick={this.handledeleteNote.bind(this, note.id)}>&times; </button></div>
               </div>
            )
         }
  }, this);


  //renders page content
return <div>
   <div className="panel panel-default">
      <div className="panel-body">
         <form className="form-group">
            <label for="add-note">Type your note Here</label>
            <input 
            id="add-note"
            className="form-control"
            type="text"
            value={this.state.userInput}
            placeholder="Add note here"
            onChange={this.handleuserInput.bind(this)}
            />
          </form>

            <button className="btn btn-success" onClick={this.handleAddNote.bind(this)}>Save Note </button>
            <span className="alerts">{this.state.alert}</span>
      </div>
   </div>
         <h3 className="text-center">Recently added notes</h3> <hr></hr>
      <div>
         {model.notes.length === 0 ? <h3>No new notes</h3> : listAddedNotes}
      </div>
</div>
}

//This code updates the state of the user input
handleuserInput(s)
   {
   this.setState({userInput: s.target.value})
}

//Adding new note
handleAddNote(){
   if(this.state.userInput != ''){
      AbortController.addNote({
         content:this.state.userInput,
         bgColor:this.state.color
      });
   }

   //Updating component state
   this.setState({
      userInput: ''
   });


//show note is successfully added
      if(this.state.userInput != ''){
         this.showAddAlert();
      }
}

//Delete a note
   handledeleteNote(id){
      controller.deleteNote(id);
      this.showDeleteAlert();
   }

   showAddAlert(){
      this.setState({alert: 'Your note is added!'})
      let hideAlert = setTimeout(this.hideAlert.bind(this), 1000);
   }

   showDeleteAlert(){
      this.setState({alert: 'Note is deleted!'})
      let hideAlert = setTimeout(this.hideAlert.bind(this), 1000);
   }

   hideAlert(){
      this.setState({alert: ''})
   }

}

ReactDOM.render(<Memo/>, document.querySelector('#app'));

class Controller{
   addNote(newNote){
      newNote.id = (model.notes.length + 1);
      model.notes.push(newNote);
   }


deleteNote(id){
   let new_notes = [];
   for (let i=0; i< model.notes.length; i++){
      if(model.notes[i]['id'] == id){

      }
      else {
         new_notes.push(model.notes[i]);
      }
   }
      model.notes = new_notes;
   }
}
var controller = new Controller();
