   constructor() {
      this.notes = []
   }
}

var model = new Model();

class View extends React.Component{
   constructor() {
      super();
      this.state = {
         userInput: '',
         color: 'skyblue',
         alert: ''
      };
   }

   render (){
      let listAddedNotes = Model.notes.map(function(note){
         if(note.id != null){
            return(
               <div>
                  <div className={note.bgColor + `note-box alert col-md-11`}>
                  {note.content}
               </div>
               <div className="col-md-1 text-center">
                  <button className="delete btn btn-default" onClick={this.handledeleteNote.bind(this, note.id)}>&times; </button></div>
               </div>
            )
         }
  }, this);

return <div>
   <div className="panel panel-default">
      <div className="panel-body">
         <form className="form-group">
            <label for="add-note">Type your note Here</label>
            <input 
            id="add-note"
            className="form-control"
            type="text"
            value={this.state.userInput}
            placeholder="Add note here"
            onChange={this.handleuserInput.bind(this)}
            />
          </form>

            <button className="btn btn-success" onClick={this.handleAddNote.bind(this)}>Save Note </button>
            <span className="alerts">{this.state.alert}</span>
      </div>
   </div>
