   {
   this.setState({userInput: s.target.value})
}

//Adding new note
handleAddNote(){
   if(this.state.userInput != ''){
      AbortController.addNote({
         content:this.state.userInput,
         bgColor:this.state.color
      });
   }

   //Updating component state
   this.setState({
      userInput: ''
   });


//show note is successfully added
