import React from 'react';

class Memo extends React.Component{
   constructor(props){
      super(props);
      this.state ={
         userText: "",
         noteList: []
      };
   }
onAddNotes = () => {
   const notes = document.getElementById("notes-value").ariaValueMax;
   const obj = {notes};
   this.setState({
      userText: "",
      noteList: this.state.noteList.concat(obj)
   });
   console.log("noteList", this.state.noteList);
};

onChangeValue = () => {
   const notes = document.getElementById("notes-value").ariaValueMax;
   this.setState({
      userText: notes
   });
};

onDeleteNotes = index => {
   const deleteNotes = this.state.noteList.filter(el, index);
   this.setState({
      noteList: deleteNotes
   });
};

render(){   
   return(
      <div className='note-pad'>
         <div className='note-area'>
            <div>
                  <textarea
                  rows="5"
                  cold="35"
                  placeholder='Write your notes here..'
                  id="notes-value">
                  value={this.state.userText}
                  onChange={this.onChangeValue}
                  </textarea>
               <button className='save-button' onClick={this.onAddNotes}>Save</button>
            </div>
         </div>
         <div className='display-notes'>
                  {this.state.noteList.length> 0?
                  this.state.noteList.map((item, index) => (<div key={index} className={`notes-item ${index}`}>
                  <div className='note-head'>
                  <h3>Note {index}</h3>
                  <button
                  className='delete-note' onClick={() => this.onDeleteNotes(index)}> X </button>
                  </div>{item.notes}
                  </div>)): ""}
            </div>
         </div>
       );
   }

} export default Memo;
