import React from 'react';
import '../index.css';

class Home extends React.Component{
    constructor(){
        super();
        this.state = {color: "green"}
    }




render ()
 {
    return(
        <div className='background-box' >
         <label htmlFor='select-color'>Select Color for background</label>
        <div id="select-color">
         <input className='color-radio' type={'radio'}  onClick={() => this.selectGreen()} defaultChecked={this.state.color == 'green' ? 'checked' : ''}/> Green &nbsp;
         <input className='color-radio' type={'radio'} onClick={() => this.selectRed()} checked={this.state.color == 'red' ? 'checked' : ''  }/> Red &nbsp;
        <input className='color-radio' type={'radio'} onClick={() => this.selectBlue()} checked={this.state.color == 'blue' ? 'checked' : '' }/> Blue &nbsp;
        <input className='color-radio'type={'radio'} onClick={() => this.selectOrange()} checked={this.state.color == 'orange' ? 'checked' : '' }/> Orange &nbsp; 
        </div>       
       {/* <div style={{ 
        display: 'flex',
        justifyContent: 'Center',
        alignItems: 'Center',
        height: '100vh'}}
        > 
        </div> */}
        <h1>Welcome Home</h1>
     
    </div>
    );

};

selectGreen(){
    this.setState({color: 'green'})
    }

selectRed(){
    this.setState({color:'red'})
    }

selectBlue(){
    this.setState({color:'blue'})
    }

selectOrange(){
    this.setState({color:'orange'})
    }
}

export default Home;
