import React, { Component } from 'react'

export default class Memo2 extends Component {
    constructor() {
        super();
        this.state = {
           userInput: '',
           color: 'skyblue',
           alert: ''
        };
     }
    //  All life cycle hooks listed here 

    //  followed by all your created funtions 
    handleuserInput(){}
    handleAddNote(){}


  render() {
      {/* Add all your visual data  */}
    return (
    <div className="panel panel-default">
      
      <div className="panel-body">
         <form className="form-group">
            <label for="add-note">Type your note Here</label>
            <input 
            id="add-note"
            className="form-control"
            type="text"
            value={this.state.userInput}
            placeholder="Add note here"
            onChange={(e)=>this.handleuserInput(e)}
            />
          </form>
            <button className="btn btn-success" onClick={()=> this.handleAddNote()}>Save Note </button>
            <span className="alerts">{this.state.alert}</span>
      </div>



         <h3 className="text-center">Recently added notes</h3> <hr></hr>
           {/* {model.notes.length === 0 ? <h3>No new notes</h3> : listAddedNotes} */}
     
      
   </div>

      
    )
  }
}
